import axios from 'axios'
import React, { useEffect, useState } from 'react'
import { 
    BrowserRouter as Router,
    Link
 } from 'react-router-dom'

const Home = ({postsToRender}) => {

    const [posts, setPosts] = useState([])
    const [postsToShow, setPostsToShow] = useState(3)
    useEffect(() => {
        axios.get('http://localhost:3000/posts').then(res => {
            setPosts(res.data)
        })
    }, [])

    const handleMorePost = () => {
        setPostsToShow((prevValue) => prevValue + 3)
    }

    return (posts ?
        <div className="home">
            {posts.slice(0, postsToShow).map((post, index) => (
                <div className="home__item" key={index}>
                    <img src={post.img} alt="home__img" className="home__img"/>
                    <div className="home__text">
                        <div className="text--heading">{post.heading}</div><div className="space__5"></div>
                        <span className="text--aaa text--time">
                            {post.date}
                        </span>
                        <span className="text--aaa">
                            {post.category}
                        </span>
                        <div className="text--aaa"> 
                            {post.details}
                            <Link 
                                onClick={handleMorePost}
                                to="/postinfo"
                                className="home__link"
                            >Read more...
                            </Link>
                        </div>
                    </div>
                </div>
            ))}
        </div> : <> </>
    )
}

export default Home
