import axios from 'axios';
import React, { useEffect, useState } from 'react'
import { useParams } from 'react-router';

const Post = () => {
    const [posts, setPosts] = useState(null)
    const { id } = useParams();
    console.log(posts);
    useEffect(() => {
        axios.get(`https://jsonplaceholder.typicode.com/posts/${id}`).then(res => {
            console.log(res)
            setPosts(res.data)
            })
    }, [id])
    return posts?.id ?
        <div>
        {posts.img}
        </div> : <></>
}

export default Post
