import React from 'react'

const Post = () => {
    const [posts, setPosts] = useState(null)
    const { id } = useParams();
    console.log(posts);
    useEffect(() => {
        axios.get(`https://jsonplaceholder.typicode.com/posts/${id}`).then(res => {
            console.log(res)
            setPosts(res.data)
            })
    }, [id])
    return posts?.id ? <div style={{ display:'flex', textAlign:'center'}}>
        <div>
            <Card title={posts.title}>
            <CardImg top width="50%" src={posts.img} />
                <h3>{posts.author}</h3>
            </Card>
        </div>
        <div>{posts.content}</div>
    </div> : <></>
}

export default Post
